    private void selecionarBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_selecionarBtnActionPerformed
        // TODO add your handling code here:
        
        if(diretorBtn.isSelected()){
            ExcluiDiretor ed = new ExcluiDiretor();
            ed.setVisible(true);
            this.dispose();
        }else{
            if(ingressoBtn.isSelected()){
                ExcluiIngresso ei = new ExcluiIngresso();
                ei.setVisible(true);
                this.dispose();
            }
            else{
                if(pessoaBtn.isSelected()){
                    ExcluiPessoa ep = new ExcluiPessoa();
                    ep.setVisible(true);
                    this.dispose();
                }
                else{
                    if(filmeBtn.isSelected()){
                        ExcluiFilme ef = new ExcluiFilme();
                        ef.setVisible(true);
                        this.dispose();
                    }
                }
            }
        }
    }//GEN-LAST:event_selecionarBtnActionPerformed

